import './App.css';
import CardList from "../components/CardList";
import SearchName from "../components/SearchName";
import SearchSeason from "../components/SearchSeason"
// import Scroll from "../components/Scroll";
import {library} from '../library';
import React, {Component} from 'react';
import { render } from '@testing-library/react';




class App extends Component {
  constructor(){
    super()
    this.state={
      vegetable: library,
      searchfield: ''
    }
  }

  onSearchChange=(event)=> {
    this.setState({ searchfield: event.target.value })
  }

  render(){
      const filterName= this.state.vegetable.filter(vegetable=>{
      return vegetable.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
    });
    const filterSeason= this.state.vegetable.filter(library=>{
      return library.type.toLowerCase().includes(this.state.searchfield.toLowerCase());
    });

    return(
      <div className='tc'>
        <h1>Vegetables</h1>
        <SearchName searchChange={this.onSearchChange}/>
        <SearchSeason searchChange={this.onSearchChange}/>
        <CardList vegetables={(filterName, filterSeason)}/>
      </div>
    );
  }
}

export default App;
